import Player from './playerModel'

function Controller(isLogged) {
	this.isLogged = isLogged
}

Controller.prototype.login = function (socket, data) {
	if (!data.hasOwnProperty('name'))
		throw new Error("No name property")
	var ids = Object.keys(this.isLogged)
	var value = ids.find(elem => this.isLogged[elem].name === data.name)
	if (value)
		throw new Error("Username already taken")

	var player = new Player(socket, data.name)
	this.isLogged[socket.id] = player
	console.log(`New player connected ${player.socket.id} - ${player.name}`)
	return player	
}

Controller.prototype.logout = function (socket) {
	const user = this.isLogged[socket.id]
	if (!user)
		return 
	user.disconnect()
	delete this.isLogged[socket.id]	
}

export default new Controller({})

import Board from '../Game/Board'
const events = require('events')

function Player(socket, name) {
	this.socket = socket
	this.name = name
	this.game = undefined
	this.isPlaying = false
	this.currentLobby = undefined
	this.controller = this.controller.bind(this)
	this.socket.on("CONTROLLER", this.controller)

//	console.log(`New player ${socket.id} ${name}`)
}

Player.prototype.get = function () {
	let display
	if (this.game)
		display = this.game.get()
	else
		display = undefined
	return {
		id: this.socket.id,
		name: this.name,
		isPlaying: this.isPlaying,
		display: display
	}
}

Player.prototype.Notify = function (event, data) {
	this.socket.emit(event, data)
}

Player.prototype.join = function (Lobby) {
	this.socket.join(Lobby.id)
	this.currentLobby = Lobby
}

Player.prototype.leave = function (Lobby) {
	console.log('this current lobby:', this.currentLobby.id)
	this.socket.leave(this.currentLobby.id)
	this.currentLobby = undefined
}

Player.prototype.disconnect = function () {
	if (!this.currentLobby)
		return ;
	this.currentLobby.leaveGame(this)
}

Player.prototype.controller = function (data) {
//	console.log(`${this.socket.id} - ${data}`)
	if (!this.game)
		return 
	if (data === 'LEFT')
		this.game.left()
	else if (data === 'UP')
		this.game.rotate()
	else if (data === 'RIGHT')
		this.game.right()
	else if (data === 'DOWN')
		this.game.down()
	else if (data === 'SPACE')
		this.game.place()

}


Player.prototype.initGame = function (mode) {
	this.eventEmitter = new events.EventEmitter()
	this.game = new Board(this.eventEmitter, mode)
	this.socket.emit("DISPLAY", this.game.get())	
}

Player.prototype.start = function (getPiece, sendMallus, end) {
	this.eventEmitter.on('display', (data) => { this.socket.emit("DISPLAY", data) })
	this.eventEmitter.on('mallus', () => { sendMallus(this.socket.id) })
	this.eventEmitter.on('add', (data) => {
		if(!this.game)
			return 
		if (!this.game.add(getPiece(this.socket.id, data)))
			end(this.socket.id)
	})

	this.socket.on('disconnect', () => {
		if (this.game)
			this.stopGame()
	})
	this.socket.on('QUIT', () => {
		this.game.stop()
		this.eventEmitter.removeAllListener()
	})
	this.game.start()
	this.isPlaying = true
}


Player.prototype.stopGame = function () {
	if (!this.game)
		return false
	this.game.stop()
	this.isPlaying = false
//	delete this.game // to check

	return true
}

// THIS ONE TOO
Player.prototype.getMalus = function () {
	if (!this.isPlaying)
		return false;
	if (!this.game.setMalus()) {
		clearInterval(this.itr)
		return false
	}
	return true
}

export default Player

import io from 'socket.io'
import debug from 'debug'
import _ from 'lodash'
import params from '../params'

import { connectToDatabase } from './config'

import { fetch, join, leave, start } from './rooms/roomsAPI'
import userController from './player/playerController'

const logerror = debug('tetris:error')
, loginfo = debug('tetris:info')

connectToDatabase(params.server.db)

const server = io.listen(params.server.port)
loginfo(`Listening on: ${params.server.url}`)

server.on('connection', function(socket) {
	loginfo("Socket connected: YOO " + socket.id)
	socket.on('login', async function(data) {
		let user
		try {
			if (!data)
				return
			user = userController.login(socket, data)
			socket.emit("login", { name: user.name })
			if (!_.isEmpty(data.room))
				join(io, user, data)
			socket.on('FETCH', (data) => fetch(server, user, data))
			socket.on('JOIN', (data) => join(server, user, data))
			socket.on("LEAVE", (data) => leave(user, data))
			socket.on("START", (data) => start(user, data))
		} catch (err) {
			socket.emit("login", { err: err.message })
		}
	})

	socket.on('disconnect', function() {
		loginfo('Socket disconnected: ' + socket.id);
		userController.logout(socket)			
	})
})

const params = {
