const mongoose = require('mongoose');

export async function connectToDatabase(uri) {
    try {
		await mongoose.connect(uri, { urlNewUrlParser: true });
		await mongoose.set('useFindAndModify', false);
    } catch (err) {
		Promise.reject(err)
    }
}

export async function closeDatabase() {
	try {
		await mongoose.connection.close()
	} catch (err) {
		console.log(err)
		process.exit(1)
	}
}

import { Rooms } from './roomsDAL';
import { create, find, restoreRooms } from './roomsController'

export async function fetch(io, user, data) {
    try {
		if (!data)
			return
		let skip = data.hasOwnProperty('skip') ? data.skip : 0
		let limit = data.hasOwnProperty('limit') ? data.limit : 0

		let rooms = await Rooms.read({}, {}, skip, limit)

		let list = await restoreRooms(io)
		user.Notify("FETCH", { rooms })
    } catch (err) {
		console.log(err)
    }
}

export async function join(io, user, data) {
	let room
	try {
		console.log("Room info::", data)
		if (data.room.id)
			room = await find(data.room.id)
		else {
			room = await create(io, data.room)
			user.Notify("CREATED", { room: true })
		}
		room.newPlayer(user)
	} catch(err) {
		user.Notify("JOINING", { state: "JOINED", err })
		Promise.reject(err)
	}
}

export async function leave(user, data) {
	let room
	try {
		console.log("Room info:::", data)
		if (!data.id)
			return
		room = await find(data.id)
		await room.leaveGame(user)
	} catch (err) {
		user.Notify("LEAVE", { err: err.message })
		Promise.reject(err)
	}
}

export async function start(user, data) {
	let room
	try {
		if (!data)
			return
		room = await find(data)
		room.startGame(user)
	} catch(err) {
		user.Notify("START", err.message)
		Promise.reject(err)
	}
}

const mongoose = require('mongoose');

const Schema = mongoose.Schema

const roomSchema = new Schema({
    id: String,
    name: String,
    mode: String,
    open: Boolean
}, {timestamps: { createdAt: 'created_at'}});


roomSchema.statics = {
    async create(newRoom) {
		try {
			const duplicate = this.findOne({ id: newRoom.id })
			if (!duplicate)
				Promise.reject(new Error("room already exist"))
			var room = new Rooms(newRoom)
			const p = await room.save()
			return p
		} catch (err) {
			return Promise.reject(err)
		}
    },

    async readOne(id) {
		try {
			const room = await this.findOne({ id: id }).exec()
			if (!room)
				return new Error("room not found")
			return room.toObject();
		} catch(err) {
			return Promise.reject(err);
		}
    },

    async read(query, fields, skip, limit) {
		try {
			const rooms = await this.find(query, fields).skip(skip).limit(limit).sort({"created_at": '1'}).exec()
			if (!rooms.length) {
				return []
			}
			return rooms.map(room => room.toObject())
		} catch(err) {
			return Promise.reject(err)
		}
    },

    async update(id, roomUpdate) {
		try {
			let res = await this.updateOne({id: id}, roomUpdate)
			return res.nModified
		} catch (err) {
			return Promise.reject(err)
		}
    },

	async deleteRoom(id) {
		try {
			var p = await this.deleteOne({id: id}).exec()
			return p.deletedCount
		} catch (err) {
			Promise.reject(err)
		}
	}
}

const Rooms = mongoose.model("Rooms", roomSchema);

module.exports = {
    Rooms
}

import uuidv4 from 'uuid'
import Room from './roomsModel';
import { Rooms as roomDb } from './roomsDAL';
import _ from 'lodash'

const Rooms = {};

/*
** Launch at Start
*/
export async function restoreRooms(io) {
    try {
		if (!_.isEmpty(Rooms))
			return null;
		var all = await roomDb.read({}, {}, 0, 0)
		_.map(all, function (v) {
		  var newRoom = new Room(io, v.id, v.name, v.mode)
			Rooms[v.id] = newRoom
		})
		return all
    } catch (err) {
		console.log(err)
		Promise.reject(err)
    }
}

export async function create(io, room) {
    try {
		var id = uuidv4()
		var r = new Room(io, id, room.name, room.mode)
		Rooms[id] = r
		room["id"] = id
		var obj = await roomDb.create(room)
		return r
    } catch (err) {
		console.log("Room creation failure!..")
		Promise.reject(err)
    }
}

export async function find(id) {
    if (!id)
		return undefined
	let room = Rooms[id]
	if (!room)
		Promise.reject("room not found")
    return room
}

export function deleteRoom(id) {
    console.log("deleting room:", id)
    if (!Rooms[id]) {
		return new Error("Room doesn't exist")
    }
    delete Rooms[id]
    console.log("room deleted")
    return null
}

import _ from 'lodash'
import Tetraminos from '../Game/tetraminos'
import { isPlaying } from './helpers'
export default function Lobby(io, id, name, mode) {
	this.ios = io
	this.io = io.sockets.in(id)
	this.id = id
	this.name = name
	this.mode = selectMode(mode)
	this.host = undefined
	this.users = {}
	this.start = false
	this.pieces = []
	this.broad = setInterval(function () {
		this.startBroadcast()
	}.bind(this), 500)
	this.ping('CHECK')
}

Lobby.prototype.kill = function() {
	if (this.broad)
		clearInterval(this.broad)
	if (this.start) {
		this.io.in(this.id).emit("LEAVE", { state: "QUIT", msg: "Server may intentionaly kill this lobby" })
		_.map(this.users, (user) => {
			user.stopGame()
		})
	}
	return true
}

Lobby.prototype.newPlayer = function (user) {
	if (this.start)
		return false
	this.users[user.socket.id] = user
	user.join(this)
	user.Notify("JOINED", { state: "JOINED", room: {id: this.id, name: this.name, mode: this.mode }})
	if (this.host === undefined) {
		this.host = user
		user.Notify("HOST", { host: true })
	} else
		user.Notify("HOST", { host: false })
	user.initGame(this.mode)
	this.ping('CHECK')
	return true
}

Lobby.prototype.startGame = function (user) {
	if (user.socket.id === this.host.socket.id) {
		this.start = true
		this.io.in(this.id).emit("START", { start: this.start })
		var ids = Object.keys(this.users)
		ids.map((id, k) => {
			this.users[id].initGame(this.mode)
			this.users[id].isPlaying = true;
			this.users[id].start(this.pieceCallback.bind(this),
								 this.mallusCallback.bind(this),
								 this.endGameCallback.bind(this))
		})
		this.ping('CHECK')
		return true
	}
	return false
}
Lobby.prototype.resetLobby = function () {
	this.start = false
	this.pieces = []
	this.ping('CHECK')
}

Lobby.prototype.leaveGame = function (user) {
	var leaver = this.users[user.socket.id]
	if (!leaver) {
		console.log("THROOOOOW")
		throw new Error("User not found")
	}
	user.leave(this.id) //
	if (leaver.isPlaying)
		leaver.stopGame()

	// delete user.game
	delete this.users[user.socket.id] // BECUSE OF U
	console.log("Number of player::", this.users.length)
	if (!isPlaying(this.users))
		this.resetLobby()
	if (user.socket.id === this.host.socket.id) {
		this.host = undefined
		var newHost = _.sample(this.users)
		if (newHost) {
			console.log("new HOST")
			this.host = newHost
			this.host.Notify("HOST", { host: true })
			// this.host.Notify("START", { start: this.start })
		}
	}
	user.Notify("LEAVE", { state: "QUIT" })
	this.ping('CHECK')
}



Lobby.prototype.endGameCallback = function (id) {
	var user = this.users[id]
	if (!user) 
		return
	if (user.isPlaying)
		user.stopGame()
	var stillPlaying = _.find(this.users, function(u) { return u.isPlaying === true })
	if (_.isEmpty(stillPlaying)) {
		this.resetLobby()
		user.Notify("GAMEOVER", { winner: true })
		this.host.Notify("START", { start: this.start })
	} else {
		user.Notify("START", { start: !this.start})
		user.Notify("GAMEOVER", { winner: false }) // maybe not necessary
	}
}

Lobby.prototype.startBroadcast = function () {
	var id = Object.keys(this.users)
	if (!id)
		return
	var arr = []
	id.map((v, k) => {
		arr.push(this.users[v].get())
	})
	this.io.in(this.id).emit("PLAYERS", arr)
}

Lobby.prototype.pieceCallback = function (id, nbr) {
	var p = this.pieces[nbr]
	if (!p) {
		p = Tetraminos()
		this.pieces.push(p)
	}
	return p
}

Lobby.prototype.mallusCallback = function (userID) {
	var ids = Object.keys(this.users)
	if (ids.length === 0)
		return false
	ids.map((id, index) => {
		if (id === userID)
			return
		this.users[id].getMalus()
	})
	return true
}

Lobby.prototype.ping = function (event) {
	var mode = this.mode === true ? "invisible" : "classic"
	var nbr = Object.keys(this.users).length
	let info = {
		id: this.id,
		name: this.name,
		mode: mode,
		nbrUser: nbr,
		isStarted: this.start
	}
	console.log('[PING] EVENT: ', event, info)
	try {
		this.io.emit(event, { room : info })
	} catch(err) {
		console.log(err)
	}
}

function selectMode(mode) {
	if (mode === "classic")
		return false;
	else if (mode === "invisible")
		return true;
	return false;
}

export function isPlaying(users) {
	const keys = Object.keys(users)
	if (!users)
		return undefined
	return keys.find(el => users[el].isPlaying === true)
}

